UPDATE user INNER JOIN address ON user.id_address = address.id SET first_name = '" + OneUser.Firstname + "', last_name = '" + OneUser.Lastname + "', email = '" + OneUser.Email + "', phone = '" + OneUser.Phone + "', is_alive = '" + strconv.FormatBool(OneUser.IsAlive) + "', street = '" + OneUser.Address.Street + "', city = '" + OneUser.Address.City + "', state = '" + OneUser.Address.State + "', country = '" + OneUser.Address.Country + "', zip_code = '" + OneUser.Address.ZipCode + "', id_role = (SELECT role.id FROM role WHERE role.role = '" + OneUser.Role + "') WHERE user.id = '" + id_user + "'; "